// NOVA FUNÇÃO ADICIONADA - Extrator de Links de Mensagens com Texto
function extractLinkFromFormattedMessage(message) {
    try {
        // Regex para encontrar URLs em diferentes formatos de mensagem
        const patterns = [
            // Formato: **[Texto]** (https://url)
            /\*\*\[.*?\]\*\*\s*\((https?:\/\/[^\s\)]+)\)/g,
            // Formato: [Texto] (https://url) 
            /\[.*?\]\s*\((https?:\/\/[^\s\)]+)\)/g,
            // Formato: (https://url)
            /\((https?:\/\/[^\s\)]+)\)/g,
            // URL direta
            /(https?:\/\/[^\s\)]+)/g
        ];

        for (const pattern of patterns) {
            const matches = [...message.matchAll(pattern)];
            if (matches.length > 0) {
                // Pega o primeiro link encontrado e limpa caracteres indesejados
                let url = matches[0][1];
                url = url.replace(/[,\.\)]+$/, '');

                // Verificar se é uma URL de mídia válida
                if (isValidURL(url)) {
                    return {
                        success: true,
                        url: url,
                        detectedType: detectFileType(url),
                        method: 'formatted_message_extraction'
                    };
                }
            }
        }

        return null;
    } catch (error) {
        console.log("Erro na extração de link formatado:", error);
        return null;
    }
}

// Extrator Específico para Tags de Mídia
// Formato esperado: <imagem>url</imagem>, <video>url</video>, etc.

// Mapeamento de tipos de mídia
const MEDIA_TYPES = {
    'imagem': ['jpg', 'jpeg', 'png', 'webp', 'gif'],
    'video': ['mp4', 'webm', 'avi', 'mov'],
    'audio': ['mp3', 'wav', 'ogg', 'm4a'],
    'documento': ['pdf', 'doc', 'docx']
};

// Função para detectar tipo de arquivo pela extensão
function detectFileType(url) {
    const extension = url.split('.').pop().toLowerCase();

    for (const [type, extensions] of Object.entries(MEDIA_TYPES)) {
        if (extensions.includes(extension)) {
            return type;
        }
    }

    return 'unknown';
}

// Função para validar URL
function isValidURL(url) {
    try {
        // Verificar se é uma URL válida
        const urlPattern = /^https:\/\/[^\s<>"'()]+\.[a-zA-Z0-9]+$/i;
        if (!urlPattern.test(url.trim())) {
            return false;
        }

        // Verificar se tem extensão de arquivo válida
        const extension = url.split('.').pop().toLowerCase();
        const allValidExtensions = Object.values(MEDIA_TYPES).flat();

        return allValidExtensions.includes(extension);
    } catch (error) {
        return false;
    }
}

// Função para extrair conteúdo de qualquer tag de mídia
function extractMediaFromTags(rawInput) {
    try {
        // Regex para capturar qualquer tag de mídia
        const mediaTagRegex = /<(imagem|video|audio|documento|midia)>(.*?)<\/\1>/gi;
        const matches = [...rawInput.matchAll(mediaTagRegex)];

        if (matches.length === 0) {
            return null;
        }

        // Processar todas as tags encontradas
        const results = [];

        for (const match of matches) {
            const tagType = match[1].toLowerCase();
            const url = match[2].trim();

            if (isValidURL(url)) {
                const detectedType = detectFileType(url);

                results.push({
                    tagType: tagType,
                    detectedType: detectedType,
                    url: url,
                    valid: true
                });
            } else {
                results.push({
                    tagType: tagType,
                    url: url,
                    valid: false,
                    error: 'URL inválida'
                });
            }
        }

        return results;
    } catch (error) {
        console.log("Erro na extração de tags:", error);
        return null;
    }
}

// Função principal específica para tag <imagem>
function extractImageURL(rawInput) {
    try {
        const imageTagRegex = /<imagem>(.*?)<\/imagem>/i;
        const match = rawInput.match(imageTagRegex);

        if (match && match[1]) {
            const url = match[1].trim();

            if (isValidURL(url)) {
                return {
                    success: true,
                    url: url,
                    tipo: 'imagem',
                    detectedType: detectFileType(url)
                };
            } else {
                return {
                    success: false,
                    error: 'URL da imagem inválida',
                    url: url
                };
            }
        }

        return null;
    } catch (error) {
        console.log("Erro na extração de imagem:", error);
        return null;
    }
}

