// Pega a mensagem do cliente do campo correto
const messageText = $input.item.json.texto || '';

// Função para converter nome do mês para número
function getMonthNumber(monthName) {
  const months = {
    'janeiro': '01', 'jan': '01',
    'fevereiro': '02', 'fev': '02',
    'março': '03', 'mar': '03',
    'abril': '04', 'abr': '04',
    'maio': '05', 'mai': '05',
    'junho': '06', 'jun': '06',
    'julho': '07', 'jul': '07',
    'agosto': '08', 'ago': '08',
    'setembro': '09', 'set': '09',
    'outubro': '10', 'out': '10',
    'novembro': '11', 'nov': '11',
    'dezembro': '12', 'dez': '12'
  };

  return months[monthName.toLowerCase().trim()] || null;
}

// Função principal para detectar e extrair datas
function processDateFromMessage(text) {
  const textLower = text.toLowerCase();

  // Palavras que indicam interesse em viajar/reservar
  const travelKeywords = [
    'viajar', 'viagem', 'ir para', 'quero ir', 'pretendo',
    'reservar', 'reserva', 'fechar', 'fechamento', 'confirmar',
    'para o dia', 'no dia', 'dia', 'data', 'quando',
    'agendado', 'marcado', 'planejando', 'programando'
  ];

  // Verifica contexto de viagem/reserva
  const hasTravelContext = travelKeywords.some(keyword => textLower.includes(keyword));

  // Arrays para armazenar todas as datas encontradas
  const datesFound = [];

  // Padrão 1: DD/MM/YYYY ou DD-MM-YYYY ou DD.MM.YYYY
  const pattern1 = /(\d{1,2})[\/\-\.](\d{1,2})[\/\-\.](\d{4})/g;
  let matches = text.matchAll(pattern1);
  for (const match of matches) {
    const day = match[1].padStart(2, '0');
    const month = match[2].padStart(2, '0');
    const year = match[3];
    datesFound.push({
      formatted: `${year}-${month}-${day}`,
      original: match[0],
      type: 'full',
      day: parseInt(day),
      month: parseInt(month),
      year: parseInt(year)
    });
  }

  // Padrão 2: DD/MM ou DD-MM (assume ano atual ou próximo)
  const pattern2 = /(\d{1,2})[\/\-](\d{1,2})(?![\/\-\d])/g;
  matches = text.matchAll(pattern2);
  for (const match of matches) {
    const day = match[1].padStart(2, '0');
    const month = match[2].padStart(2, '0');
    const currentDate = new Date();
    const currentYear = currentDate.getFullYear();
    const currentMonth = currentDate.getMonth() + 1;

    // Se o mês já passou, assume próximo ano
    let year = currentYear;
    if (parseInt(month) < currentMonth) {
      year = currentYear + 1;
    }

    datesFound.push({
      formatted: `${year}-${month}-${day}`,
      original: match[0],
      type: 'partial',
      day: parseInt(day),
      month: parseInt(month),
      year: year
    });
  }

  // Padrão 3: "dia DD de MÊS" ou "DD de MÊS"
  const pattern3 = /(?:dia\s+)?(\d{1,2})\s+de\s+(janeiro|fevereiro|março|abril|maio|junho|julho|agosto|setembro|outubro|novembro|dezembro|jan|fev|mar|abr|mai|jun|jul|ago|set|out|nov|dez)/gi;
  matches = text.matchAll(pattern3);
  for (const match of matches) {
    const day = match[1].padStart(2, '0');
    const monthNum = getMonthNumber(match[2]);
    if (monthNum) {
      const currentDate = new Date();
      const currentYear = currentDate.getFullYear();
      const currentMonth = currentDate.getMonth() + 1;

      let year = currentYear;
      if (parseInt(monthNum) < currentMonth) {
        year = currentYear + 1;
      }

      datesFound.push({
        formatted: `${year}-${monthNum}-${day}`,
        original: match[0],
        type: 'text',
        day: parseInt(day),
        month: parseInt(monthNum),
        year: year
      });
    }
  }

  // Padrão 4: Datas relativas
  const today = new Date();

  if (textLower.includes('hoje')) {
    datesFound.push({
      formatted: today.toISOString().split('T')[0],
      original: 'hoje',
      type: 'relative',
      day: today.getDate(),
      month: today.getMonth() + 1,
      year: today.getFullYear()
    });
  }

  if (textLower.includes('amanhã')) {
    const tomorrow = new Date(today);
    tomorrow.setDate(tomorrow.getDate() + 1);
    datesFound.push({
      formatted: tomorrow.toISOString().split('T')[0],
      original: 'amanhã',
      type: 'relative',
      day: tomorrow.getDate(),
      month: tomorrow.getMonth() + 1,
      year: tomorrow.getFullYear()
    });
  }

  if (textLower.includes('depois de amanhã')) {
    const dayAfter = new Date(today);
    dayAfter.setDate(dayAfter.getDate() + 2);
    datesFound.push({
      formatted: dayAfter.toISOString().split('T')[0],
      original: 'depois de amanhã',
      type: 'relative',
      day: dayAfter.getDate(),
      month: dayAfter.getMonth() + 1,
      year: dayAfter.getFullYear()
    });
  }

  // Padrão 5: "próxima semana", "semana que vem"
  if (textLower.includes('próxima semana') || textLower.includes('semana que vem')) {
    const nextWeek = new Date(today);
    nextWeek.setDate(nextWeek.getDate() + 7);
    datesFound.push({
      formatted: nextWeek.toISOString().split('T')[0],
      original: 'próxima semana',
      type: 'relative',
      day: nextWeek.getDate(),
      month: nextWeek.getMonth() + 1,
      year: nextWeek.getFullYear()
    });
  }

  // Padrão 6: "final de MÊS", "início de MÊS"
  const monthPattern = /(final|fim|início|começo)\s+de\s+(janeiro|fevereiro|março|abril|maio|junho|julho|agosto|setembro|outubro|novembro|dezembro|jan|fev|mar|abr|mai|jun|jul|ago|set|out|nov|dez)/gi;
  matches = text.matchAll(monthPattern);
  for (const match of matches) {
    const position = match[1].toLowerCase();
    const monthNum = getMonthNumber(match[2]);
    if (monthNum) {
      const currentYear = new Date().getFullYear();
      let day = position.includes('final') || position.includes('fim') ? '28' : '01';

      datesFound.push({
        formatted: `${currentYear}-${monthNum}-${day}`,
        original: match[0],
        type: 'approximate',
        day: parseInt(day),
        month: parseInt(monthNum),
        year: currentYear,
        isApproximate: true
      });
    }
  }

  // Determina a melhor data (prioriza datas completas e contexto de viagem)
  let bestDate = null;
  if (datesFound.length > 0) {
    // Prioriza datas completas sobre parciais
    bestDate = datesFound.find(d => d.type === 'full') || 
               datesFound.find(d => d.type === 'text') ||
               datesFound.find(d => d.type === 'partial') ||
               datesFound[0];
  }

  // Calcula confiança
  let confidence = 'none';
  if (bestDate) {
    if (hasTravelContext && bestDate.type === 'full') {
      confidence = 'high';
    } else if (hasTravelContext || bestDate.type === 'full') {
      confidence = 'medium';
    } else {
      confidence = 'low';
    }
  }

  return {
    hasDate: bestDate !== null,
    date: bestDate ? bestDate.formatted : null,
    dateOriginal: bestDate ? bestDate.original : null,
    dateType: bestDate ? bestDate.type : null,
    allDatesFound: datesFound,
    confidence: confidence,
    hasTravelContext: hasTravelContext,
    shouldUpdateCRM: bestDate !== null && confidence !== 'none',
    originalMessage: text
  };
}

