    mensagemFinal = `ÁUDIO MENCIONADO (ID: ${audioMencionadoId})

mensagem mencionada anterior: "${mensagemAtual}"

INSTRUÇÃO: O cliente está respondendo a um áudio anterior. Você precisa transcrever o áudio usando o ID fornecido antes de responder.`;
  } else {
    mensagemFinal = `Mensagem mencionada anterior: "${mensagemMencionada}"

Referenciando mensagem mencionada: "${mensagemAtual}"

Responda considerando que a segunda mensagem se refere à primeira.`;
  }
}

// Retorna o resultado
return {
  mensagemTratada: mensagemFinal,
  temMencao: mensagemMencionada ? true : false,
  tipoMencao: tipoMencao,
  mensagemOriginal: mensagemAtual,
  mensagemMencionada: mensagemMencionada,
  audioMencionadoId: audioMencionadoId,
  // Para usar no próximo nó se for áudio
  precisaTranscrever: tipoMencao === "audio"
};

// Versão simplificada - assumindo que temos 2 inputs
const messages = $items("Code12");
const transcriptions = $input.all("OpenAI");

let transcriptionIndex = 0;

return messages.map(msg => {
  if (msg.json.isAudio === true && transcriptionIndex < transcriptions.length) {
    const text = transcriptions[transcriptionIndex].json.text;
    transcriptionIndex++;

    return {
      json: {
        message: text,
        fromMe: msg.json.fromMe,
        type: msg.json.type,
        wasAudio: true
      }
    };
  }

  return {
    json: msg.json
  };
});

// Pegar todos os items do Code6
const allMessages = $('Code9').all();

// Formatar cada mensagem para adicionar na thread
const formattedMessages = allMessages.map((item, index) => {
  // Extrair dados da mensagem
  const message = item.json.message;
  const type = item.json.type; // 'user' ou 'assistant'
  const fromMe = item.json.fromMe;

  // Determinar o role correto
  // Se type existe, usar ele. Senão, basear em fromMe
  let role = type || (fromMe ? 'assistant' : 'user');
  if (role === 'lead') {
    role = 'user';
  }

  return {
    json: {
      // Dados para adicionar na thread
      role: role,
      content: message,

      // Metadados adicionais
      messageIndex: index,
      isUser: role === 'user',
      isAssistant: role === 'assistant',
      fromMe: fromMe,

      // Dados para controle
      totalMessages: allMessages.length,
      isLastMessage: index === allMessages.length - 1,
      isFirstMessage: index === 0
    }
  };
});

// Retornar cada mensagem como um item separado
return formattedMessages;

// Pegar os records do input
const messages = $('BUSCA CONVERSA3').first().json.messages.records;

// Processar mensagens de forma simplificada
const processedMessages = messages.map(msg => {
  // Extrair conteúdo baseado no tipo
  let content = '';

  switch(msg.messageType) {
    case 'conversation':
      content = msg.message.conversation;
      break;
    case 'audioMessage':
      // Para áudio, usar o ID dentro de key
      content = msg.key.id;
      break;
    case 'documentMessage':
      content = `[Documento: ${msg.message.documentMessage?.fileName || 'arquivo'}]`;
      break;
    case 'imageMessage':
      content = `[Imagem${msg.message.imageMessage?.caption ? ': ' + msg.message.imageMessage.caption : ''}]`;
      break;
    case 'videoMessage':
      content = `[Vídeo${msg.message.videoMessage?.caption ? ': ' + msg.message.videoMessage.caption : ''}]`;
      break;
    default:
      content = '[Mensagem]';
  }

  // Retornar objeto simplificado
  return {
    message: content,
    fromMe: msg.key.fromMe,
    timestamp: msg.messageTimestamp,
    type: msg.key.fromMe ? 'assistant' : 'lead',
    isAudio: msg.messageType === 'audioMessage'
  };
});

// Ordenar por timestamp (mais antigas primeiro)
processedMessages.sort((a, b) => a.timestamp - b.timestamp);

// Retornar no formato esperado pelo n8n
return processedMessages.map(msg => ({
  json: {
    message: msg.message,
    fromMe: msg.fromMe,
    type: msg.type,
    isAudio: msg.isAudio
  }
}));

// Pegar todos os itens do node anterior
const items = $input.all();

// Filtrar apenas os áudios
const audioMessages = items.filter(item => item.json.isAudio === true);

// Se não houver áudios, retornar um item vazio para o IF processar
if (audioMessages.length === 0) {
  return [{
    json: {
      hasAudio: false,
      message: "Sem áudios para processar"
    }
  }];
}

// Retornar CADA ÁUDIO como um item separado
return audioMessages.map(audio => ({
  json: {
    audioId: audio.json.message,  // ID do áudio no Evolution
    fromMe: audio.json.fromMe,
    type: audio.json.type,
    isAudio: true
  }
}));
